  _normal(eq, p) {
    const fp = eq(p.x, p.y, p.z);
    const x = (eq(p.x + EPS, p.y, p.z) - fp) / EPS;
    const y = (eq(p.x, p.y + EPS, p.z) - fp) / EPS;
    const z = (eq(p.x, p.y, p.z + EPS) - fp) / EPS;
    const l = Math.sqrt(x * x + y * y + z * z);
    return {
      x: x / l,
      y: y / l,
      z: z / l
    };
  }

  _surfacePoint(eq, v1, v2) {
    // linear interpolation

    const mu = -v1.value / (v2.value - v1.value);

    return {
      x: v1.position.x + mu * (v2.position.x - v1.position.x),
      y: v1.position.y + mu * (v2.position.y - v1.position.y),
      z: v1.position.z + mu * (v2.position.z - v1.position.z)
    };
  }

  _lookupEdge(equation, v1, v2, edges) {

    const e1 = edges.get(v1);

    if (!e1) {
      const e1 = new WeakMap();

      const p = this._surfacePoint(equation, v1, v2);
      const vertIndex = this.pushVertex(p, this._normal(equation, p));

      e1.set(v2, vertIndex);
      edges.set(v1, e1);

      return vertIndex;
    }

    const e2 = e1.get(v2);

    if (!e2) {
      const p = this._surfacePoint(equation, v1, v2);
      const vertIndex = this.pushVertex(p, this._normal(equation, p));

      e1.set(v2, vertIndex);

      return vertIndex;
    }

    return e2;

  }

  setResolution(resolution) {
    this.resolution = resolution;

    this._values = [];

    for (let i = 0; i <= resolution; i++) {
      this._values[i] = [];
      for (let j = 0; j <= resolution; j++) {
        this._values[i][j] = [];
        for (let k = 0; k <= resolution; k++) {
          this._values[i][j][k] = {
            position: {x: 0, y: 0, z: 0},
            value: 0
          };
        }
      }
    }
  }

  triangulate(equation) {

    for (let i = 0; i <= this.resolution; i++) {
      const x = this.radius * (2 * i / this.resolution - 1) + this.center.x;
      for (let j = 0; j <= this.resolution; j++) {
        const y = this.radius * (2 * j / this.resolution - 1) + this.center.y;
        for (let k = 0; k <= this.resolution; k++) {
          const z = this.radius * (2 * k / this.resolution - 1) + this.center.z;
          const v = this._values[i][j][k];
          v.position.x = x;
          v.position.y = y;
          v.position.z = z;
          v.value = equation(x, y, z);
        }
      }
    }

    const edges = new WeakMap();

    const vertList = [];

    for (let i = 0; i < this.resolution; i++) {
      for (let j = 0; j < this.resolution; j++) {
        for (let k = 0; k < this.resolution; k++) {

          const v0 = this._values[i][j + 1][k];
          const v1 = this._values[i + 1][j + 1][k];
          const v2 = this._values[i + 1][j][k];
          const v3 = this._values[i][j][k];
          const v4 = this._values[i][j + 1][k + 1];
          const v5 = this._values[i + 1][j + 1][k + 1];
          const v6 = this._values[i + 1][j][k + 1];
          const v7 = this._values[i][j][k + 1];

          let cubeIndex = 0;

          if (v0.value < 0) cubeIndex |= 1;
          if (v1.value < 0) cubeIndex |= 2;
          if (v2.value < 0) cubeIndex |= 4;
          if (v3.value < 0) cubeIndex |= 8;
          if (v4.value < 0) cubeIndex |= 16;
          if (v5.value < 0) cubeIndex |= 32;
          if (v6.value < 0) cubeIndex |= 64;
          if (v7.value < 0) cubeIndex |= 128;

          const activeEdges = this._edgeTable[cubeIndex];

          if (activeEdges & 1) vertList[0] = this._lookupEdge(equation, v0, v1, edges);
          if (activeEdges & 2) vertList[1] = this._lookupEdge(equation, v2, v1, edges);
          if (activeEdges & 4) vertList[2] = this._lookupEdge(equation, v3, v2, edges);
          if (activeEdges & 8) vertList[3] = this._lookupEdge(equation, v3, v0, edges);
          if (activeEdges & 16) vertList[4] = this._lookupEdge(equation, v4, v5, edges);
          if (activeEdges & 32) vertList[5] = this._lookupEdge(equation, v6, v5, edges);
          if (activeEdges & 64) vertList[6] = this._lookupEdge(equation, v7, v6, edges);
          if (activeEdges & 128) vertList[7] = this._lookupEdge(equation, v7, v4, edges);
          if (activeEdges & 256) vertList[8] = this._lookupEdge(equation, v0, v4, edges);
          if (activeEdges & 512) vertList[9] = this._lookupEdge(equation, v1, v5, edges);
          if (activeEdges & 1024) vertList[10] = this._lookupEdge(equation, v2, v6, edges);
          if (activeEdges & 2048) vertList[11] = this._lookupEdge(equation, v3, v7, edges);

          const tri = this._triTable[cubeIndex];

          for (let i = 0; i < tri.length; i += 3) {
            const vert0 = vertList[tri[i]];
            const vert1 = vertList[tri[i + 1]];
            const vert2 = vertList[tri[i + 2]];
            this.pushTriangle(vert0, vert1, vert2);
          }
        }
      }
    }
  }
